import React, {Component} from 'react';
import { Form } from 'react-bootstrap';

class Fabrication extends Component {
    constructor(props) {
        super(props);
        this.state = {name: '', description: '', model: '', step: '', validation: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState(
            {[event.target.name] : event.target.value}
        );
      }
    
    handleSubmit(event) {
        const {name, description, model, step, validation} = this.state;
        event.preventDefault();
        alert(`Procédé créé 
        Nom: ${name} 
        Description : ${description}
        Modèle : ${model}
        Étapes : ${step}
        Validation : ${validation}`);
    }

    render() {
        return (
            <div className="w-50 m-auto">
                <h1>Ajouter un procédé</h1>
                <Form onSubmit={this.handleSubmit}>
                    <label htmlFor="name">
                    Nom:
                    <input type="text" name="name" value={this.state.name} onChange={this.handleChange} />
                    </label>
                    <label htmlFor="description">
                        Description:
                        <textarea type="text" name="description" value={this.state.description} onChange={this.handleChange}></textarea>
                    </label>
                    <input type="submit" value="Envoyer" />
                    <label htmlFor="model">
                        Modèle:
                        <input type="text" name="model" value={this.state.model} onChange={this.handleChange}/>
                    </label>
                    <label htmlFor="step">
                        Étapes:
                        <input type="text" name="step" value={this.state.step} onChange={this.handleChange}/>
                    </label>
                    <label htmlFor="validation">
                        Description des validations:
                        <input type="text" name="validation" value={this.state.validation} onChange={this.handleChange}/>
                    </label>
                </Form>
            </div>
        );
      }
}

export default Fabrication;
