import React, {Component} from 'react';
import '../../App.css';
import {FormLabel, Form, FormControl, FormGroup, Button, Table } from 'react-bootstrap';

class ModelForm extends Component {
    constructor(props) {
        super(props);
        this.state = {erreur: null, name: '', description: '', prix: '', gamme: '', models: []};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        fetch("data.json", {
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            }
        }
        ).then(res => res.json())
        .then(
            (result) => {
                console.log(result);
                this.setState({
                    models: result.models
                });
                console.log(this.state)
            },
            (error) => {
                this.setState({
                    erreur: error.message
                });
            }
        )
    }

    handleChange(event) {
        this.setState(
            {[event.target.name] : event.target.value}
        );
    }
    
    handleSubmit(event) {
        event.preventDefault();
        fetch("https://jsonplaceholder.typicode.com/sfsef", {
            method: "post",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(this.state)
        });
        console.log(this.state);
        this.setState({name: '', description: '', prix: '', gamme: ''});
    }

    render() {
            if (this.state.erreur) {
                return <div>Erreur : {this.state.erreur}</div>
            } else {
                return(
                    <div>
                        <div className="modelTable">
                            <Table striped bordered hover>
                                <thead>
                                    <tr>
                                        <th>Nom</th>
                                        <th>Description</th>
                                        <th>Prix</th>
                                        <th>Gamme</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.state.models.map(model => (
                                        <tr>
                                            <td>{model.name}</td>
                                            <td>{model.description}</td>
                                            <td>{model.prix}</td>
                                            <td>{model.gamme}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </Table>
                        </div>
                        <div className="form-display">
                            <h1>Ajouter un modèle</h1>
                            <Form id="freezebeeForm" onSubmit={this.handleSubmit}>
                                <FormGroup className="mb-3" controlId="name">
                                    <FormLabel>
                                        Nom
                                    </FormLabel>
                                    <FormControl required type="text" placeholder="Nom du freezebee" name="name" value={this.state.name} onChange={this.handleChange}>
                                    </FormControl>
                                </FormGroup>
                                <FormGroup className="mb-3" controlId="description">
                                    <FormLabel>
                                        Description
                                    </FormLabel>
                                    <FormControl required type="text" placeholder="Description du freezebee" name="description" value={this.state.description} onChange={this.handleChange}>
                                    </FormControl>
                                </FormGroup>
                                <FormGroup className="mb-3" controlId="prix">
                                    <FormLabel>
                                        Prix
                                    </FormLabel>
                                    <FormControl pattern="[0-9]" required type="number" placeholder="Prix du freezebee" name="prix" value={this.state.prix} onChange={this.handleChange}>
                                    </FormControl>
                                </FormGroup>
                                <FormGroup className="mb-3" controlId="gamme">
                                    <FormLabel>
                                        Gamme
                                    </FormLabel>
                                    <FormControl required type="text" placeholder="Gamme du freezebee" name="gamme" value={this.state.gamme} onChange={this.handleChange}>
                                    </FormControl>
                                </FormGroup>
                                <Button variant="primary" type="submit">
                                    Envoyer
                                </Button>
                            </Form>
                        </div>
                    </div>
                )
            }
      }
}

export default ModelForm;
