import React, {Component} from 'react';
import '../../App.css';
import { Form, FormControl, FormGroup, FormLabel, Button, FormSelect } from 'react-bootstrap';

class TestForm extends Component {
    constructor(props) {
        super(props);
        this.state = {name: '', description: '', type: '', processes: [], selectedProcess: '', tests: []};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        fetch("data.json", {
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            }
        }
        ).then(res => res.json())
        .then(
            (result) => {
                console.log(result);
                this.setState({
                    processes: result.processes,
                    selectedProcess: result.processes[0],
                    tests: result.tests
                });
                console.log(this.state.processes)
            },
            (error) => {
                this.setState({
                    erreur: error.message
                });
            }
        )
    }

    handleChange(event) {
        this.setState(
            {[event.target.name] : event.target.value}
        );
      }
    
    handleSubmit(event) {
        event.preventDefault();
        console.log(this.state);
        this.setState({name: '', description: '', type: '', selectedProcess: ''});
    }

    render() {
        return (
            <div className="form-display">
                <h1>Créer un test de validation</h1>
                <Form id="testForm" onSubmit={this.handleSubmit}>
                <FormGroup className="mb-3" controlId="name">
                    <FormLabel>
                        Nom
                    </FormLabel>
                    <FormControl required type="text" name="name" placeholder="Nom du test" value={this.state.name} onChange={this.handleChange}>
                    </FormControl>
                </FormGroup>
                <FormGroup className="mb-3" controlId="description">
                    <FormLabel>
                        Description
                    </FormLabel>
                    <FormControl required type="text" placeholder="Description du test" name="description" value={this.state.description} onChange={this.handleChange}>
                    </FormControl>
                </FormGroup>
                <FormGroup className="mb-3" controlId="selectedProcess">
                    <FormLabel>
                        Procédés
                    </FormLabel>
                    <FormSelect name="selectedProcess" value={this.state.selectedProcess} onChange={this.handleChange}>
                        {this.state.processes.map(process => (
                                <option value={process.name} key={process.name}>
                                    {process.name}
                                </option>
                            )
                        )}
                    </FormSelect>
                </FormGroup>
                    <Button variant="primary" type="submit">
                        Envoyer
                    </Button>
                </Form>
            </div>
        );
      }
}

export default TestForm;
