import React, {Component} from 'react';
import Form, { FormControl, FormGroup, FormLabel, FormText } from 'react-bootstrap'

class FreezeBee extends Component {
    constructor(props) {
        super(props);
        this.state = {name: '', description: '', pUHT: '', gamme: '', ingredients: '', grammage: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState(
            {[event.target.name] : event.target.value}
        );
      }
    
    handleSubmit(event) {
        const {name, description, pUHT, gamme, ingredients, grammage} = this.state;
        event.preventDefault();
        alert(`Modèle créé 
        Nom: ${name} 
        Description : ${description}
        pUHT : ${pUHT}
        Gamme : ${gamme}
        Ingredients : ${ingredients}
        Grammage : ${grammage}`);
    }

    render() {
        return (
            <div>
                <h1>Ajouter un modèle</h1>
                <Form onSubmit={this.handleSubmit}>
                    <FormGroup className="mb-3" controlId="name">
                        <FormLabel>
                            Nom
                        </FormLabel>
                        <FormControl type="text" placeholder="Nom du freezebee" name="name" value={this.state.name} onChange={this.handleChange}>
                        </FormControl>
                    </FormGroup>
                    <label htmlFor="name">
                    Nom:
                    <input type="text"  />
                    </label>
                    <label htmlFor="description">
                        Description:
                        <textarea type="text" name="description" value={this.state.description} onChange={this.handleChange}></textarea>
                    </label>
                    <input type="submit" value="Envoyer" />
                    <label htmlFor="pUHT">
                        pUHT:
                        <input type="number" name="pUHT" value={this.state.pUHT} onChange={this.handleChange}/>
                    </label>
                    <label htmlFor="gamme">
                        Gamme:
                        <input type="text" name="gamme" value={this.state.gamme} onChange={this.handleChange}/>
                    </label>
                    <label htmlFor="ingredients">
                        Ingrédients:
                        <input type="text" name="ingredients" value={this.state.ingredients} onChange={this.handleChange}/>
                    </label>
                    <label htmlFor="grammage">
                        Grammage:
                        <input type="number" name="grammage" value={this.state.grammage} onChange={this.handleChange}/>
                    </label>
                </Form>
            </div>
        );
      }
}

export default FreezeBee;
