import React, {Component} from 'react';
import '../App.css';
import { Form, FormControl, FormGroup, FormLabel, Button } from 'react-bootstrap';

class Ingredient extends Component {
    constructor(props) {
        super(props);
        this.state = {name: '', description: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState(
            {[event.target.name] : event.target.value}
        );
      }
    
    handleSubmit(event) {
        const {name, description} = this.state;
        event.preventDefault();
        console.log(this.state);
    }

    render() {
        return (
            <div className="form-display">
                <h1>Créer un ingrédient</h1>
                <Form id="ingredientForm" onSubmit={this.handleSubmit}>
                <FormGroup className="mb-3" controlId="name">
                        <FormLabel>
                            Nom
                        </FormLabel>
                        <FormControl required type="text" name="name" placeholder="Nom de l'ingrédient" value={this.state.name} onChange={this.handleChange}>
                        </FormControl>
                    </FormGroup>
                    <FormGroup className="mb-3" controlId="description">
                        <FormLabel>
                            Description
                        </FormLabel>
                        <FormControl required type="text" placeholder="Description de l'ingrédient" name="description" value={this.state.description} onChange={this.handleChange}>
                        </FormControl>
                    </FormGroup>
                    <Button variant="primary" type="submit">
                        Envoyer
                    </Button>
                </Form>
            </div>
        );
      }
}

export default Ingredient;
