import React, {Component} from 'react';
import '../../App.css';
import { Form, FormControl, FormGroup, FormLabel, Button, Col } from 'react-bootstrap';
import IngredientToModelForm from './IngredientToModelForm';

class IngredientForm extends Component {
    constructor(props) {
        super(props);
        this.state = {name: '', description: '', show: false};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState(
            {[event.target.name] : event.target.value}
        );
      }
    
    handleSubmit(event) {
        event.preventDefault();
        console.log(this.state);
    }

    showModal = e => {
        this.setState({show: !this.state.show});
    }

    onClose = e => {
        this.props.onClose && this.props.onClose(e);
      };

    render() {
        return (
            <Col className="ingredientModelCol">
                <div className="form-display">
                    <h1>Créer un ingrédient</h1>
                    <Form id="ingredientForm" onSubmit={this.handleSubmit}>
                    <FormGroup className="mb-3" controlId="name">
                            <FormLabel>
                                Nom
                            </FormLabel>
                            <FormControl required type="text" name="name" placeholder="Nom de l'ingrédient" value={this.state.name} onChange={this.handleChange}>
                            </FormControl>
                        </FormGroup>
                        <FormGroup className="mb-3" controlId="description">
                            <FormLabel>
                                Description
                            </FormLabel>
                            <FormControl required type="text" placeholder="Description de l'ingrédient" name="description" value={this.state.description} onChange={this.handleChange}>
                            </FormControl>
                        </FormGroup>
                        <Button variant="primary" type="submit">
                            Envoyer
                        </Button>
                    </Form>
                </div>
                <Button className="m-auto mt-4 d-flex" variant="primary" onClick={e => {this.showModal();}}>
                    Ajouter un ingrédrient à un modèle
                </Button>
                <IngredientToModelForm onClose={this.showModal} show={this.state.show}>

                </IngredientToModelForm>
            </Col>

        );
      }
}

export default IngredientForm;
