import React, {Component} from 'react';
import {FormLabel, Form, FormControl, FormGroup, Button } from 'react-bootstrap';

class FreezeBee extends Component {
    constructor(props) {
        super(props);
        this.state = {name: '', description: '', pUHT: '', gamme: '', ingredients: '', grammage: ''};
        const [validated, setValidated] = useState(false);

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState(
            {[event.target.name] : event.target.value}
        );
      }
    
    handleSubmit(event) {
        const form = event.currentTarget;
        if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
        } else {
            const {name, description, pUHT, gamme, ingredients, grammage} = this.state;
            event.preventDefault();
            alert(`Modèle créé 
            Nom: ${name} 
            Description : ${description}
            pUHT : ${pUHT}
            Gamme : ${gamme}
            Ingredients : ${ingredients}
            Grammage : ${grammage}`);
        }
    }

    render() {
        return (
            <div className="w-50 m-auto">
                <h1>Ajouter un modèle</h1>
                <Form onSubmit={this.handleSubmit}>
                    <FormGroup className="mb-3" controlId="name">
                        <FormLabel>
                            Nom
                        </FormLabel>
                        <FormControl required type="text" placeholder="Nom du freezebee" name="name" value={this.state.name} onChange={this.handleChange}>
                        </FormControl>
                    </FormGroup>
                    <FormGroup className="mb-3" controlId="description">
                        <FormLabel>
                            Description
                        </FormLabel>
                        <FormControl required type="text" placeholder="Description du freezebee" name="description" value={this.state.description} onChange={this.handleChange}>
                        </FormControl>
                    </FormGroup>
                    <FormGroup className="mb-3" controlId="pUHT">
                        <FormLabel>
                            pUHT
                        </FormLabel>
                        <FormControl required type="number" placeholder="Prix du freezebee" name="pUHT" value={this.state.pUHT} onChange={this.handleChange}>
                        </FormControl>
                    </FormGroup>
                    <FormGroup className="mb-3" controlId="gamme">
                        <FormLabel>
                            Gamme
                        </FormLabel>
                        <FormControl required type="text" placeholder="Gamme du freezebee" name="gamme" value={this.state.gamme} onChange={this.handleChange}>
                        </FormControl>
                    </FormGroup>
                    <FormGroup className="mb-3" controlId="ingredients">
                        <FormLabel>
                            Ingredients
                        </FormLabel>
                        <FormControl required type="text" placeholder="Ingredients du freezebee" name="ingredients" value={this.state.ingredients} onChange={this.handleChange}>
                        </FormControl>
                    </FormGroup>
                    <FormGroup className="mb-3" controlId="grammage">
                        <FormLabel>
                            Grammage
                        </FormLabel>
                        <FormControl required type="text" placeholder="Grammage du freezebee" name="grammage" value={this.state.grammage} onChange={this.handleChange}>
                        </FormControl>
                    </FormGroup>
                    <Button variant="primary" type="submit">
                        Envoyer
                    </Button>
                </Form>
            </div>
        );
      }
}

export default FreezeBee;
