import React from 'react';
import ReactDOM from 'react-dom';

class Ingredient extends React.Component {
    constructor(props) {
        super(props);
        this.state = {name: "", description: ""};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({name: event.target.name, description: event.target.description});
      }
    
    handleSubmit(event) {
        alert('A name was submitted: ' + this.state.name + ' et la description est ' + this.state.description);
        event.preventDefault();
    }

    render() {
        return (
          <form onSubmit={this.handleSubmit}>
            <label htmlFor="name">
              Nom:
              <input type="text" name="name" value={this.state.name} onChange={this.handleChange} />
            </label>
            <label htmlFor="description">
                Description:
                <textarea type="text" name="description" value={this.state.description} onChange={this.handleChange}></textarea>
            </label>
            <input type="submit" value="Envoyer" />
          </form>
        );
      }
}

export default Ingredient;

