import React, {Component} from 'react';
import '../App.css';
import { Form, FormControl, FormGroup, FormLabel, Button } from 'react-bootstrap';

class Fabrication extends Component {
    constructor(props) {
        super(props);
        this.state = {name: '', description: '', model: '', step: '', validation: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState(
            {[event.target.name] : event.target.value}
        );
      }
    
    handleSubmit(event) {
        const {name, description, model, step, validation} = this.state;
        event.preventDefault();
        console.log(this.state);
    }

    render() {
        return (
            <div className="form-display">
                <h1>Ajouter un procédé</h1>
                <Form id="fabricationForm" onSubmit={this.handleSubmit}>
                    <FormGroup className="mb-3" controlId="name">
                        <FormLabel>
                            Nom
                        </FormLabel>
                        <FormControl required type="text" name="name" placeholder="Nom du procédé" value={this.state.name} onChange={this.handleChange}>
                        </FormControl>
                    </FormGroup>
                    <FormGroup className="mb-3" controlId="description">
                        <FormLabel>
                            Description
                        </FormLabel>
                        <FormControl required type="text" placeholder="Description du procédé" name="description" value={this.state.description} onChange={this.handleChange}>
                        </FormControl>
                    </FormGroup>
                    <FormGroup className="mb-3" controlId="model">
                        <FormLabel>
                            Modèle
                        </FormLabel>
                        <FormControl required type="text" name="model" placeholder="Nom du modèle" value={this.state.model} onChange={this.handleChange}>
                        </FormControl>
                    </FormGroup>
                    <FormGroup className="mb-3" controlId="step">
                        <FormLabel>
                            Étapes
                        </FormLabel>
                        <FormControl required type="text" name="step" placeholder="Étapes du procédé" value={this.state.step} onChange={this.handleChange}>
                        </FormControl>
                    </FormGroup>
                    <FormGroup className="mb-3" controlId="validation">
                        <FormLabel>
                            Validation
                        </FormLabel>
                        <FormControl required type="text" name="validation" placeholder="Validation des tests" value={this.state.validation} onChange={this.handleChange}>
                        </FormControl>
                    </FormGroup>
                    <Button variant="primary" type="submit">
                        Envoyer
                    </Button>
                </Form>
            </div>
        );
      }
}

export default Fabrication;
