import React, {Component} from 'react';
import {FormLabel, Form, FormControl, FormGroup, Button, FormSelect } from 'react-bootstrap';

class FreezeBee extends Component {
    constructor(props) {
        super(props);
        this.state = {erreur: null, name: '', description: '', pUHT: '', gamme: '', ingredients: [], grammage: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    getData() {
        fetch("./mock/data.json", {
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            }
        }
        ).then(res => res.json())
        .then(
            (result) => {
                this.setState({
                    ingredients: result.ingredients
                });
            },
            // (error) => {
            //     this.setState({
            //         error: erreur.message
            //     });
            // }
        )
    }

    handleChange(event) {
        this.setState(
            {[event.target.name] : event.target.value}
        );
    }
    
    handleSubmit(event) {
        const {name, description, pUHT, gamme, ingredients, grammage, erreur} = this.state;
        event.preventDefault();
        console.log(this.state);
        event.target.reset();
        // this.state = {name: '', description: '', pUHT: '', gamme: '', ingredients: '', grammage: ''};
    }

    render() {
            // if (erreur) {
            //     return <div>Erreur : {erreur}</div>
            // } else {
                return(
                    <div className="w-50 m-auto">
                    <h1>Ajouter un mod√®le</h1>
                    <ul>
                        {ingredients.map(ingredient => (
                            <li key={ingredient.value}>
                                {ingredient.value}
                            </li>
                        ))}
                    </ul>
                    <Form id="freezebeeForm" onSubmit={this.handleSubmit}>
                        <FormGroup className="mb-3" controlId="name">
                            <FormLabel>
                                Nom
                            </FormLabel>
                            <FormControl required type="text" placeholder="Nom du freezebee" name="name" value={this.state.name} onChange={this.handleChange}>
                            </FormControl>
                        </FormGroup>
                        <FormGroup className="mb-3" controlId="description">
                            <FormLabel>
                                Description
                            </FormLabel>
                            <FormControl required type="text" placeholder="Description du freezebee" name="description" value={this.state.description} onChange={this.handleChange}>
                            </FormControl>
                        </FormGroup>
                        <FormGroup className="mb-3" controlId="pUHT">
                            <FormLabel>
                                pUHT
                            </FormLabel>
                            <FormControl required type="number" placeholder="Prix du freezebee" name="pUHT" value={this.state.pUHT} onChange={this.handleChange}>
                            </FormControl>
                        </FormGroup>
                        <FormGroup className="mb-3" controlId="gamme">
                            <FormLabel>
                                Gamme
                            </FormLabel>
                            <FormControl required type="text" placeholder="Gamme du freezebee" name="gamme" value={this.state.gamme} onChange={this.handleChange}>
                            </FormControl>
                        </FormGroup>
    
                        <FormGroup className="mb-3" controlId="ingredients">
                            <FormLabel>
                                Ingredients
                            </FormLabel>
                            <FormSelect name="ingredients" value={this.state.ingredients} onChange={this.handleChange}>
                                <option value="grapefruit">Pamplemousse</option>
                                <option value="lime">Citron vert</option>
                                <option value="coconut">Noix de coco</option>
                                <option value="mango">Mangue</option>
                            </FormSelect>
                        </FormGroup>
                        {/* <FormGroup className="mb-3" controlId="ingredients">
                            <FormLabel>
                                Ingredients
                            </FormLabel>
                            <FormControl required type="text" placeholder="Ingredients du freezebee" name="ingredients" value={this.state.ingredients} onChange={this.handleChange}>
                            </FormControl>
                        </FormGroup> */}
                        <FormGroup className="mb-3" controlId="grammage">
                            <FormLabel>
                                Grammage
                            </FormLabel>
                            <FormControl required type="number" placeholder="Grammage du freezebee" name="grammage" value={this.state.grammage} onChange={this.handleChange}>
                            </FormControl>
                        </FormGroup>
                        <Button variant="primary" type="submit">
                            Envoyer
                        </Button>
                    </Form>
                </div>
                )
            // }
      }
}

export default FreezeBee;
