import React, {Component} from 'react';

class Ingredient extends Component {
    constructor(props) {
        super(props);
        this.state = {name: '', description: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState(
            {[event.target.name] : event.target.value}
        );
      }
    
    handleSubmit(event) {
        const {name, description} = this.state;
        event.preventDefault();
        alert(`Ingrédient créé \n Nom: ${name} \n Description : ${description}`);
    }

    render() {
        return (
            <div>
                <h1>Créer un ingrédient</h1>
                <Form onSubmit={this.handleSubmit}>
                <FormGroup className="mb-3" controlId="name">
                        <FormLabel>
                            Nom
                        </FormLabel>
                        <FormControl type="text" name="name" placeholder="Nom de l'ingrédient" value={this.state.name} onChange={this.handleChange}>
                        </FormControl>
                    </FormGroup>
                    <FormGroup className="mb-3" controlId="description">
                        <FormLabel>
                            Description
                        </FormLabel>
                        <FormControl type="text" placeholder="Description de l'ingrédient" name="description" value={this.state.description} onChange={this.handleChange}>
                        </FormControl>
                    </FormGroup>
                    <label htmlFor="name">
                    Nom:
                    <input type="text" name="name" value={this.state.name} onChange={this.handleChange} />
                    </label>
                    <label htmlFor="description">
                        Description:
                        <textarea type="text" name="description" value={this.state.description} onChange={this.handleChange}></textarea>
                    </label>
                    <Button variant="primary" type="submit">
                        Envoyer
                    </Button>
                </Form>
            </div>
        );
      }
}

export default Ingredient;
