import React, {Component} from 'react';
import '../App.css';
import {FormLabel, Form, FormControl, FormGroup, Button, FormSelect } from 'react-bootstrap';

class FreezeBee extends Component {
    constructor(props) {
        super(props);
        this.state = {erreur: null, name: '', description: '', pUHT: '', gamme: '', ingredients: [], grammage: '', selectedIngredient: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        fetch("data.json", {
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            }
        }
        ).then(res => res.json())
        .then(
            (result) => {
                console.log(result);
                this.setState({
                    ingredients: result.ingredients,
                    selectedIngredient: result.ingredients[0]
                });
                console.log(this.state.ingredients)
            },
            (error) => {
                this.setState({
                    erreur: error.message
                });
            }
        )
    }

    handleChange(event) {
        this.setState(
            {[event.target.name] : event.target.value}
        );
    }
    
    handleSubmit(event) {
        event.preventDefault();
        console.log(this.state);
    }

    render() {
            if (this.state.erreur) {
                return <div>Erreur : {this.state.erreur}</div>
            } else {
                return(
                    <div className="form-display">
                    <h1>Ajouter un mod√®le</h1>
                    <Form id="freezebeeForm" onSubmit={this.handleSubmit}>
                        <FormGroup className="mb-3" controlId="name">
                            <FormLabel>
                                Nom
                            </FormLabel>
                            <FormControl required type="text" placeholder="Nom du freezebee" name="name" value={this.state.name} onChange={this.handleChange}>
                            </FormControl>
                        </FormGroup>
                        <FormGroup className="mb-3" controlId="description">
                            <FormLabel>
                                Description
                            </FormLabel>
                            <FormControl required type="text" placeholder="Description du freezebee" name="description" value={this.state.description} onChange={this.handleChange}>
                            </FormControl>
                        </FormGroup>
                        <FormGroup className="mb-3" controlId="pUHT">
                            <FormLabel>
                                pUHT
                            </FormLabel>
                            <FormControl required type="number" placeholder="Prix du freezebee" name="pUHT" value={this.state.pUHT} onChange={this.handleChange}>
                            </FormControl>
                        </FormGroup>
                        <FormGroup className="mb-3" controlId="gamme">
                            <FormLabel>
                                Gamme
                            </FormLabel>
                            <FormControl required type="text" placeholder="Gamme du freezebee" name="gamme" value={this.state.gamme} onChange={this.handleChange}>
                            </FormControl>
                        </FormGroup>
    
                        <FormGroup className="mb-3" controlId="selectedIngredient">
                            <FormLabel>
                                Ingredients
                            </FormLabel>
                            <FormSelect name="selectedIngredient" value={this.state.selectedIngredient} onChange={this.handleChange}>
                                {this.state.ingredients.map(ingredient => (
                                        <option value={ingredient.nom} key={ingredient.nom}>
                                            {ingredient.nom}
                                        </option>
                                    )
                                )}
                            </FormSelect>
                        </FormGroup>
                        <FormGroup className="mb-3" controlId="grammage">
                            <FormLabel>
                                Grammage
                            </FormLabel>
                            <FormControl required type="number" placeholder="Grammage du freezebee" name="grammage" value={this.state.grammage} onChange={this.handleChange}>
                            </FormControl>
                        </FormGroup>
                        <Button variant="primary" type="submit">
                            Envoyer
                        </Button>
                    </Form>
                </div>
                )
            }
      }
}

export default FreezeBee;
