import './App.css';
import React, {Component} from 'react';
import IngredientForm from './pages/ingredientPage/IngredientForm';
import HomePage from './pages/homePage';
import ModelForm from './pages/modelPage/ModelForm';
import {BrowserRouter as Router, Switch, Route, Link, Redirect, withRouter} from 'react-router-dom';
import ProcedeForm from './pages/procedePage/ProcedeForm';
import {Navbar, NavbarBrand, Container, Image, Nav, Button} from 'react-bootstrap';
import Logo from './assets/logo.png';
import NavbarToggle from 'react-bootstrap/esm/NavbarToggle';
import NavbarCollapse from 'react-bootstrap/esm/NavbarCollapse';
import StepForm from './pages/stepPage/StepForm';
import TestForm from './pages/testPage/TestForm';
import LoginForm from './pages/loginPage';

const fetching = require('./encryption/fetching');

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {token: null};
    console.log('App');
  }

  componentDidMount() {
    console.log('Mounted');
    this.getCacheData();
  }

  componentWillUnmount() {
    console.log('To unmount');
  }

  getCacheData = async () => {
    let url = 'https://localhost:3000';
    let cacheStorage = await caches.open('Token');
    let cachedResponse = await cacheStorage.match(url);
    let data = await cachedResponse?.json();
    if (!data) data = false;
    console.log('get cache');
    console.log('data: ' + data);
    console.log('state: ' + this.state.token);
    this.setState({token: data});
  }

  clearCacheData = () => {
    caches.keys().then((names) => {
      names.forEach((name) => {
        caches.delete(name);
      });
    });
    alert('Cache cleared');
    this.setState({token: null});
  }

  routeChange = (event) => {
    let path = event.target.name;
    this.props.history.replace(path);
  }

  render() {
    if (this.state.token == false) {
      console.log('token: false');
      return (
        <Router>
          <Navbar fixed="top" collapseOnSelect expand="lg" bg="dark" variant="dark">
            <Container>
              <Image src={Logo} className="mx-2" alt="KillerBee" height="35" />
              <NavbarBrand as={Link} to={'/'}>KillerBee</NavbarBrand>
              <NavbarToggle aria-controls="responsive-navbar-nav" />
              <NavbarCollapse className="justify-content-md-end" id="responsive-navbar-nav">
                <Nav>
                  <Button name="login" onClick={this.routeChange} variant="primary">Connexion</Button>
                </Nav>
              </NavbarCollapse>
            </Container>
          </Navbar>
          <Switch>
            <Route exact path='/' component={HomePage} />
            <Route exact path='/login' component={LoginForm} />
            <Redirect to={'/'}></Redirect>
          </Switch>
        </Router>
      );
    } else if (this.state.token) {
      console.log('token: not null');
      return (
        <Router>
          <Navbar fixed="top" collapseOnSelect expand="lg" bg="dark" variant="dark">
            <Container>
              <Image src={Logo} className="mx-2" alt="KillerBee" height="35" />
              <NavbarBrand as={Link} to={'/'}>KillerBee</NavbarBrand>
              <NavbarToggle aria-controls="responsive-navbar-nav" />
              <NavbarCollapse className="justify-content-md-end" id="responsive-navbar-nav">
                <Nav>
                  <NavbarBrand as={Link} to={'/freezebee'}>Freezebee</NavbarBrand>
                  <NavbarBrand as={Link} to={"/fabrication"}>Fabrication</NavbarBrand>
                  <NavbarBrand as={Link} to={"/ingredient"}>Ingrédients</NavbarBrand>
                  <NavbarBrand as={Link} to={"/etape"}>Étapes</NavbarBrand>
                  <NavbarBrand as={Link} to={"/test"}>Tests et validation</NavbarBrand>
                  <Button as={Link} onClick={() => this.clearCacheData()} to={'/'} variant="primary">Déconnexion</Button>
                </Nav>
              </NavbarCollapse>
            </Container>
          </Navbar>
          <Switch>
            <Route exact path='/' component={HomePage} />
            <Route path='/ingredient' component={IngredientForm} />
            <Route path='/freezebee' component={ModelForm} />
            <Route path='/fabrication' component={ProcedeForm} />
            <Route path='/etape' component={StepForm} />
            <Route path='/test' component={TestForm} />
          </Switch>
        </Router>
      );
    } else {
      console.log('token: null');
      return (
        <Router>
          <Navbar fixed="top" collapseOnSelect expand="lg" bg="dark" variant="dark">
            <Container>
              <Image src={Logo} className="mx-2" alt="KillerBee" height="35" />
              <NavbarBrand as={Link} to={'/'}>KillerBee</NavbarBrand>
              <NavbarToggle aria-controls="responsive-navbar-nav" />
            </Container>
          </Navbar>
          <Switch>
            <Route exact path='/' component={HomePage} />
            <Redirect to={'/'}></Redirect>
          </Switch>
        </Router>
      );
    }
  }
}

export default App;
