import React, {Component} from 'react';
import '../../App.css';
import { Form, FormControl, FormGroup, FormLabel, Button, Row, Col } from 'react-bootstrap';

class IngredientToModelForm extends Component {
    constructor(props) {
        super(props);
        this.state = {models: [], selectedModel: '', ingredients: [], selectedIngredient: '', grammage: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

        const showHideClassName = show ? "modal display-block" : "modal display-none";
    }

    componentDidMount() {
        fetch("data.json", {
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            }
        }
        ).then(res => res.json())
        .then(
            (result) => {
                console.log(result);
                this.setState({
                    models: result.models,
                    selectedModel: result.models[0],
                    ingredients: result.ingredients,
                    selectedIngredient: result.ingredients[0]
                });
                console.log(this.state)
            },
            (error) => {
                this.setState({
                    erreur: error.message
                });
            }
        )
    }

    handleChange(event) {
        this.setState(
            {[event.target.name] : event.target.value}
        );
      }
    
    handleSubmit(event) {
        event.preventDefault();
        console.log(this.state);
    }

    render() {
        return (
            <div>
                <Modal
                    {...this.props}
                    size="lg"
                    aria-labelledby="contained-modal-title-vcenter"
                    centered
