import React, {Component} from 'react';
import '../../App.css';
import { Button, FormLabel, FormControl, Form, FormGroup, FormSelect, Modal } from 'react-bootstrap';

class IngredientToModelForm extends Component {
    constructor(props) {
        super(props);
        this.state = {models: [], selectedModel: '', ingredients: [], selectedIngredient: '', grammage: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    onClose = e => {
        this.props.onClose && this.props.onClose(e);
      };

    componentDidMount() {
        fetch("data.json", {
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            }
        }
        ).then(res => res.json())
        .then(
            (result) => {
                this.setState({
                    models: result.models,
                    selectedModel: result.models[0],
                    ingredients: result.ingredients,
                    selectedIngredient: result.ingredients[0]
                });
            },
            (error) => {
                this.setState({
                    erreur: error.message
                });
            }
        )
    }

    handleChange(event) {
        this.setState(
            {[event.target.name] : event.target.value}
        );
      }
    
    handleSubmit(event) {
        event.preventDefault();
        this.onClose(event);
        this.setState({grammage: ''});
    }

    render() {
        if(!this.props.show) {
            return null;
        }
        return (
            <div>
                <div className="form-display">
                    <h1>Ajouter un ingrédient à un modèle</h1>
                    <Form id="ingredientForm" onSubmit={this.handleSubmit}>
                        <FormGroup className="mb-3" controlId="selectedModel">
                            <FormLabel>
                                Modèle
                            </FormLabel>
                            <FormSelect name="selectedModel" value={this.state.selectedModel} onChange={this.handleChange}>
                                {this.state.models.map(model => (
                                        <option value={model.name} key={model.name}>
                                            {model.name}
                                        </option>
                                    )
                                )}
                            </FormSelect>
                        </FormGroup>
                        <FormGroup className="mb-3" controlId="selectedIngredient">
                            <FormLabel>
                                Ingredients
                            </FormLabel>
                            <FormSelect name="selectedIngredient" value={this.state.selectedIngredient} onChange={this.handleChange}>
                                {this.state.ingredients.map(ingredient => (
                                        <option value={ingredient.name} key={ingredient.name}>
                                            {ingredient.name}
                                        </option>
                                    )
                                )}
                            </FormSelect>
                        </FormGroup>
                        <FormGroup className="mb-3" controlId="grammage">
                            <FormLabel>
                                Grammage
                            </FormLabel>
                            <FormControl pattern="[0-9]" required type="number" placeholder="Grammage du freezebee" name="grammage" value={this.state.grammage} onChange={this.handleChange}>
                            </FormControl>
                        </FormGroup>
                        <Button variant="primary" type="submit">
                            Envoyer
                        </Button>
                    </Form>
                </div>
            </div>
            
        );
      }
}

export default IngredientToModelForm;
