import React, {Component} from 'react';
import '../App.css';
import HomePage from '../pages/homePage';
import {Button, FormLabel, FormControl, Form, FormGroup} from 'react-bootstrap';
import {BrowserRouter as Router, Switch, Route, Link, Redirect} from 'react-router-dom';
import App from '../App';
import { useHistory } from 'react-router';

const fetching = require('../encryption/fetching');

class LoginForm extends Component {
    constructor(props) {
        super(props);
        this.state = {username: '', password: '', token: null};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        console.log('Login');
    }

    handleChange(event) {
        this.setState(
            {[event.target.name]: event.target.value}
        );
    }

    handleSubmit(event) {
        event.preventDefault();
        console.log(this.state);
        fetching.CreateConnection('localhost:8080', '/Connection/Request/', (err, result) => {
            if (!err) {
                fetching.FetchAPI('localhost:8080', '/LogIn/', {utilisateur: this.state.username, motDePasse: this.state.password}, (err, result) => {
                    if (!err) {
                        // this.addTokenIntoCache('Token', 'https://localhost:3000', result.token)
                        localStorage.setItem('token', result.token);
                        
                        console.log(this.state);
                    } else console.error(err);
                });
            } else console.error(err);
        });
    }

    addTokenIntoCache = (cacheName, url, response) => {
        const data = new Response(JSON.stringify(response));

        if ('caches' in window) {
            caches.open(cacheName).then((cache) => {
                cache.put(url, data);
                console.log(data);
            });
        }
    }

    render() {
        if (this.state.token) {
            return (
                <Router>
                    <Switch>
                        <Route exact path='/' component={App} />
                        <Redirect to={'/'}></Redirect>
                    </Switch>
                </Router>
            );
        } else {
            return (
                <div className="h-100 d-flex">
                    <div className="form-display">
                        <Form id="loginForm" onSubmit={this.handleSubmit}>
                            <FormGroup className="mb-3" controlId="username">
                                <FormLabel>
                                    Nom d'utilisateur
                                </FormLabel>
                                <FormControl required type="text" placeholder="Utilisateur" name="username" value={this.state.username} onChange={this.handleChange}>
                                </FormControl>
                            </FormGroup>
                            <FormGroup className="mb-3" controlId="password">
                                <FormLabel>
                                    Mot de passe
                                </FormLabel>
                                <FormControl required type="password" placeholder="Mot de passe" name="password" value={this.state.password} onChange={this.handleChange}>
                                </FormControl>
                            </FormGroup>
                            <Button variant="primary" type="submit">
                                Envoyer
                            </Button>
                        </Form>
                    </div>
                </div>
            );
        }
    }
}

export default LoginForm;
