import './App.css';
import React, { Component } from 'react';
import IngredientForm from './pages/ingredientPage/IngredientForm';
import HomePage from './pages/homePage';
import ModelForm from './pages/modelPage/ModelForm';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import ProcedeForm from './pages/procedePage/ProcedeForm';
import { Navbar, NavbarBrand, Container, Image, Nav } from 'react-bootstrap';
import Logo from './assets/logo.png';
import NavbarToggle from 'react-bootstrap/esm/NavbarToggle';
import NavbarCollapse from 'react-bootstrap/esm/NavbarCollapse';
import StepForm from './pages/stepPage/StepForm';
import TestForm from './pages/testPage/TestForm';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {login: false};
}

  isLogin = e => {
    this.setState({login: !this.state.login});
  }

  render() {
    return (
        <Router>
          <Navbar fixed="top" collapseOnSelect expand="lg" bg="dark" variant="dark">
            <Container>
              <Image src={Logo} className="mx-2" alt="KillerBee" height="35"/>
              <NavbarBrand as={Link} to={'/'}>KillerBee</NavbarBrand>
              <NavbarToggle aria-controls="responsive-navbar-nav" />
              <NavbarCollapse className="justify-content-md-end" id="responsive-navbar-nav">
                <Nav>
                  <NavbarBrand as={Link} to={'/freezebee'}>Freezebee</NavbarBrand>
                  <NavbarBrand as={Link} to={"/fabrication"}>Fabrication</NavbarBrand>
                  <NavbarBrand as={Link} to={"/ingredient"}>Ingrédients</NavbarBrand>
                  <NavbarBrand as={Link} to={"/etape"}>Étapes</NavbarBrand>
                  <NavbarBrand as={Link} to={"/test"}>Tests et validation</NavbarBrand>
                </Nav>
              </NavbarCollapse>
            </Container>
          </Navbar>
            <Switch>
              <Route exact path='/' component={HomePage} />
              <Route path='/ingredient' component={IngredientForm} />
              <Route path='/freezebee' component={ModelForm} />
              <Route path='/fabrication' component={ProcedeForm} />
              <Route path='/etape' component={StepForm} />
              <Route path='/test' component={TestForm} />
            </Switch>
        </Router>
    );
  }
}

export default App;
