import React, {Component} from 'react';
import '../../App.css';
import { Form, FormControl, FormGroup, FormLabel, Button, Table } from 'react-bootstrap';

class StepForm extends Component {
    constructor(props) {
        super(props);
        this.state = {name: '', description: '', steps: []};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        fetch("data.json", {
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            }
        }
        ).then(res => res.json())
        .then(
            (result) => {
                console.log(result);
                this.setState({
                    steps: result.steps,
                });
                console.log(this.state)
            },
            (error) => {
                this.setState({
                    erreur: error.message
                });
            }
        )
    }

    handleChange(event) {
        this.setState(
            {[event.target.name] : event.target.value}
        );
      }
    
    handleSubmit(event) {
        event.preventDefault();
        console.log(this.state);
        this.setState({name: '', description: ''});
    }

    render() {
        return (
            <div>
                <div className="modelTable">
                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th>Nom</th>
                                <th>Description</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.steps.map(step => (
                                <tr>
                                    <td>{step.name}</td>
                                    <td>{step.description}</td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                </div>
                <div className="form-display">
                    <h1>Créer une étape</h1>
                    <Form id="stepForm" onSubmit={this.handleSubmit}>
                    <FormGroup className="mb-3" controlId="name">
                            <FormLabel>
                                Nom
                            </FormLabel>
                            <FormControl required type="text" name="name" placeholder="Nom de l'étape" value={this.state.name} onChange={this.handleChange}>
                            </FormControl>
                        </FormGroup>
                        <FormGroup className="mb-3" controlId="description">
                            <FormLabel>
                                Description
                            </FormLabel>
                            <FormControl required type="text" placeholder="Description de l'étape" name="description" value={this.state.description} onChange={this.handleChange}>
                            </FormControl>
                        </FormGroup>
                        <Button variant="primary" type="submit">
                            Envoyer
                        </Button>
                    </Form>
                </div>
            </div>
        );
      }
}

export default StepForm;
